
var _outerFunc = module.exports = {
	GetPaths: function() {
		// --Add new file paths here
		// --Reference them with require('local/paths').GetModule(name-of-file.js)
		
		var _prepend = '../../bin';
		var _blynkFolderName = 'Blynk';
		var _operationsFoldername = 'Operations';

		var fileNames = [
			'blynk-setup.js',
			'blynk-auth.js',
			'database-operations.js',
			'date-time-operations.js',
			'mapping.js'
		]

		var paths = {}
		fileNames.forEach((name) => {
			var folderName;
			if (name.toLowerCase().includes(_operationsFoldername.toLowerCase()))
				folderName = _operationsFoldername;
			else
				folderName = _blynkFolderName;

			paths[name] = _prepend + '/' + folderName + '/' + name;
		});

		return paths;
	},

	GetModule: function(name) {
		var paths = _outerFunc.GetPaths();
		return require(paths[name]);
	}
}