
var _outerFunc = module.exports = {
	GetPaths: function() {
		// --Add new file paths here
		// --Reference them with require('local/paths').GetModule(name-of-file.js)

		var _prepend = '../../bin';
		var _blynkFolderName = 'Blynk';
		var _operationsFolderName = 'Operations';

		var fileNames = [
			'blynk-setup',
			'blynk-auth',
			'database-operations',
			'date-time-operations',
			'mapping'
		]

		var paths = {}
		fileNames.forEach((name) => {
			var folderName;
			if (FileNameIncludesFolder(name, _operationsFolderName))
				folderName = _operationsFolderName;
			else if (FileNameIncludesFolder(name, _blynkFolderName))
				folderName = _blynkFolderName;
			else
				folderName = '';

			paths[name] = (_prepend + '/' + folderName + '/' + name).replace(/(\/\/)/, '/');
		});

		return paths;

		function FileNameIncludesFolder(name, folder) {
			return name.toLowerCase().includes(folder.toLowerCase());
		}
	},

	GetModule: function(name) {
		var paths = _outerFunc.GetPaths();
		return require(paths[name]);
	}
}